  and company_id = #{companyId,jdbcType=BIGINT}

select

from bsm_company_supplier bcs
left join bsm_company_supplier_apply bcsa on bcs.company_id = bcsa.company_id and bcs.supplier_id = bcsa.supplier_id
where  bcs.company_id = #{companyId,jdbcType=BIGINT}
and bcs.supplier_id = #{supplierId,jdbcType=BIGINT}
limit 1

select

from bsm_company_supplier bcs
left join bsm_company_supplier_apply bcsa on bcs.company_id = bcsa.company_id and bcs.supplier_id = bcsa.supplier_id
where  bcs.company_id = #{companyId,jdbcType=BIGINT}
and bcs.supplier_id in

   #{supplierId}

